base:
.<%kebab%>-dialog {
    --<%kebab%>-dialog-color-base-background: initial;
    --<%kebab%>-dialog-color-base-border: initial;
    --<%kebab%>-dialog-color-base-overlay: rgba(0, 0, 0, 0.3);
}

.<%kebab%>-dialog__container {
    border-color: var(--<%kebab%>-dialog-color-base-border);
    background-color: var(--<%kebab%>-dialog-color-base-background);
}

.<%kebab%>-dialog__overlay {
    background-color: var(--<%kebab%>-dialog-color-base-overlay);
}
