base:
.<%kebab%>-drawer {
    --<%kebab%>-drawer-color-base-background: initial;
    --<%kebab%>-drawer-color-base-border: initial;
    --<%kebab%>-drawer-color-base-overlay: rgba(0, 0, 0, 0.3);
}

.<%kebab%>-drawer__container {
    border-color: var(--<%kebab%>-drawer-color-base-border);
    background-color: var(--<%kebab%>-drawer-color-base-background);
}

.<%kebab%>-drawer__overlay {
    background-color: var(--<%kebab%>-drawer-color-base-overlay);
}
