base:
.<%kebab%>-input {
    --input-color-base-border: initial;                         /* Цвет рамки инпута */
    --input-color-base-background: initial;                     /* Цвет фона */ 
    --input-color-base-text: initial;                           /* Цвет текста */
    --input-color-base-icon: initial;                           /* Цвет иконок */

    color: var(--input-color-base-text);
    border-color: var(--input-color-base-border);
}

.<%kebab%>-input__target {
    color: var(--input-color-base-text);
    background-color: var(--input-color-base-background);
}

.<%kebab%>-input__icon {
    color: var(--input-color-base-icon);
}

hover:
.<%kebab%>-input {
    --input-color-hover-border: initial;                         /* Цвет рамки при наведении */
    --input-color-hover-background: initial;                     /* Цвет фона при наведении */ 
    --input-color-hover-text: initial;                           /* Цвет текста при наведении */
    --input-color-hover-icon: initial;                           /* Цвет иконок при наведении */
}

.<%kebab%>-input:hover:not(._disabled, ._invalid, ._focused) {
    color: var(--input-color-hover-text);
    border-color: var(--input-color-hover-border);
}

.<%kebab%>-input:hover:not(._disabled, ._invalid, ._focused) .<%kebab%>-input__target {
    color: var(--input-color-hover-text);
    background-color: var(--input-color-hover-background);
}

.<%kebab%>-input:hover:not(._disabled, ._invalid, ._focused) .<%kebab%>-input__icon {
    color: var(--input-color-hover-icon);
}

pressed:
.<%kebab%>-input {
    --input-color-pressed-border: initial;                         /* Цвет рамки при нажатии */
