    --input-color-pressed-text: initial;                           /* Цвет текста при нажатии */
    --input-color-pressed-icon: initial;                           /* Цвет иконок при нажатии */
}

.<%kebab%>-input:active:not(._disabled, ._invalid, ._focused),
.<%kebab%>-input:active:hover:not(._disabled, ._invalid, ._focused) {
    color: var(--input-color-pressed-text);
    border-color: var(--input-color-pressed-border);
}

.<%kebab%>-input:active:not(._disabled, ._invalid, ._focused) .<%kebab%>-input__target,
.<%kebab%>-input:active:hover:not(._disabled, ._invalid, ._focused) .<%kebab%>-input__target {
    color: var(--input-color-pressed-text);
