base:
.pot-link {
    --link-color-base-text: initial;    /* Цвет текста */
    --link-color-base-icon: initial;    /* Цвет иконки */
    --link-color-base-line: initial;    /* Цвет линии */

    color: var(--link-color-base-text);
}

.pot-link:after {
    background-color: var(--link-color-base-line);
}

.pot-link__icon {
    color: var(--link-color-base-icon);
}

hover:
.pot-link {
    --link-color-hover-text: initial;   /* Цвет текста при наведении */
    --link-color-hover-icon: initial;   /* Цвет иконки при наведении */
    --link-color-hover-line: initial;   /* Цвет линии при наведении */
}

.pot-link:hover:not(._disabled, ._active) {
    color: var(--link-color-hover-text);
}

.pot-link:hover:not(._disabled, ._active):after {
    background-color: var(--link-color-hover-line);
}

.pot-link:hover:not(._disabled, ._active) .pot-link__icon {
    color: var(--link-color-hover-icon);
}

