base:
.<%kebab%>-radio {
    --radio-color-base-text: initial;                         /* Цвет текста */
    --radio-color-base-border: initial;                       /* Цвет рамки маркера */
    --radio-color-base-background: initial;                   /* Цвет фона маркера */
    --radio-color-base-point: initial;                        /* Цвет точки маркера */

    color: var(--radio-color-base-text);
}

.<%kebab%>-radio__marker {
    border-color: var(--radio-color-base-border);
    background-color: var(--radio-color-base-background);
    color: var(--radio-color-base-point);
}

.<%kebab%>-radio__marker:before {
    background-color: var(--radio-color-base-point);
}

