    --radio-color-hover-point: initial;                  /* Цвет точки маркера при наведении */
}

.<%kebab%>-radio:hover:not(._disabled, ._checked, ._invalid) {
    color: var(--radio-color-hover-text);
}

.<%kebab%>-radio:hover:not(._disabled, ._checked, ._invalid) .<%kebab%>-radio__marker {
    border-color: var(--radio-color-hover-border);
    background-color: var(--radio-color-hover-background);
