size:
.<%kebab%>-drawer {
    --<%kebab%>-drawer-size-padding: initial;
    --<%kebab%>-drawer-size-border: initial;
    --<%kebab%>-drawer-size-text: inherit;
    --<%kebab%>-drawer-size-shadow: initial;
}

.<%kebab%>-drawer .<%kebab%>-drawer__container {
    padding: var(--<%kebab%>-drawer-size-padding);
    border-width: var(--<%kebab%>-drawer-size-border);
    font-size: var(--<%kebab%>-drawer-size-text);
    box-shadow: var(--<%kebab%>-drawer-size-shadow);
}

radius:
.<%kebab%>-drawer {
    --<%kebab%>-drawer-radius: initial;
}

.<%kebab%>-drawer._position-left .<%kebab%>-drawer__container {
    border-radius: 0 var(--<%kebab%>-drawer-radius) var(--<%kebab%>-drawer-radius) 0;
}

.<%kebab%>-drawer._position-right .<%kebab%>-drawer__container {
    border-radius: var(--<%kebab%>-drawer-radius) 0 0 var(--<%kebab%>-drawer-radius);
}

.<%kebab%>-drawer._position-top .<%kebab%>-drawer__container {
    border-radius: 0 0 var(--<%kebab%>-drawer-radius) var(--<%kebab%>-drawer-radius);
}

.<%kebab%>-drawer._position-bottom .<%kebab%>-drawer__container {
    border-radius: var(--<%kebab%>-drawer-radius) var(--<%kebab%>-drawer-radius) 0 0;
}
